import react from "react";
import { TouchableOpacity, StyleSheet, Text } from "react-native";
import { Link, useNavigate } from "react-router-native";
export default function CustomButton({ text, onPress, colorb, to }) {
  const navigate = useNavigate();
  const handlePress = (e) => {
    e.preventDefault();
    navigate(to);
  };
  return (
    <TouchableOpacity
      style={[styles.button, { backgroundColor: colorb }]}
      onPress={handlePress}
    >
      <Text style={styles.appButtonText}>{text}</Text>
    </TouchableOpacity>
  );
}

const styles = new StyleSheet.create({
  button: {
    marginVertical: 8,
    paddingVertical: 12,
    paddingHorizontal: 40,
  },
  appButtonText: {
    fontSize: 15,
    color: "#fff",
    fontWeight: "bold",
    alignSelf: "center",
  },
});
