import react from "react";
import { TouchableOpacity, StyleSheet, Text } from "react-native";

export default function CustomButton({ text, onPress, colorb }) {
  return (
    <TouchableOpacity
      style={[styles.button, { backgroundColor: colorb }]}
      onPress={onPress}
    >
      <Text style={styles.appButtonText}>{text}</Text>
    </TouchableOpacity>
  );
}

const styles = new StyleSheet.create({
  button: {
    borderRadius: 10,
    paddingVertical: 12,
    paddingHorizontal: 40,
    marginVertical: 8,
  },
  appButtonText: {
    fontSize: 15,
    color: "#fff",
    fontWeight: "bold",
    alignSelf: "center",
  },
});
