import axios from "axios";
import react, { useEffect, useState } from "react";
import { StyleSheet, Text, TextInput, SafeAreaView, View } from "react-native";
import { useParams } from "react-router-native";

export default function Acount() {
  const { id } = useParams();
  const [user, setUser] = useState({});
  const [editable, setEditable] = useState(false);

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/user/${id}`)
      .then((e) => setUser(e.data));
  }, []);

  if (user) {
    return (
      <SafeAreaView
        style={{ flex: 1, width: "100%", padding: 20, alignItems: "center" }}
      >
        <Text>Perfil</Text>
        <View style={[styles.container]}>
          <View style={styles.boxInput}>
            <TextInput
              style={styles.input}
              defaultValue={user.email}
              editable={editable}
            />
          </View>
          <View style={styles.boxInput}>
            <TextInput
              style={styles.input}
              defaultValue={user.name}
              editable={editable}
            />
          </View>
          <View style={styles.boxInput}>
            <TextInput
              style={styles.input}
              defaultValue={user.password}
              editable={editable}
            />
          </View>
        </View>
      </SafeAreaView>
    );
  } else {
    return <Text>Loading</Text>;
  }
}

const styles = new StyleSheet.create({
  container: {
    width: "90%",
    alignItems: "center",
    padding: 20,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    backgroundColor: "white",
    borderRadius: 20,
    alignItems: "center",
  },
  boxInput: {
    width: "90%",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    backgroundColor: "white",
    marginVertical: 10,
    borderRadius: 20,
    alignItems: "center",
  },
  input: {
    fontSize: 20,
    width: "90%",
    paddingHorizontal: 4,
    textAlign: "center",
    paddingVertical: 10,
  },
});
