import axios from "axios";
import react, { useEffect, useState } from "react";
import { useNavigate } from "react-router-native";
import FormValidation from "../components/validate";
import {
  StyleSheet,
  Text,
  TextInput,
  SafeAreaView,
  View,
  Button,
} from "react-native";

export default function Acount() {
  const navigate = useNavigate();
  const [input, setinput] = useState({});
  const [error, seterror] = useState("");

  useEffect(() => {}, []);

  const handleClick = () => {
    axios.post(`http://localhost:8000/api/user`, input).then((e) => {
      alert(`${e.data.name} Creado con exito`);
      navigate(`/acount/${e.data.id}`);
    });
  };

  return (
    <SafeAreaView
      style={{
        flex: 1,
        width: "100%",
        padding: 20,
        alignItems: "center",
      }}
    >
      <View style={styles.header}>
        <Text style={styles.title}>Registrarse</Text>
      </View>

      <View style={[styles.container]}>
        <Text style={styles.label}>Nombre</Text>

        <View style={[styles.boxInput]}>
          <TextInput
            style={styles.input}
            onChangeText={(e) => {
              e &&
                setinput((prev) => {
                  return { ...prev, name: e };
                });
              seterror(FormValidation({ ...input, name: e }));
            }}
          />
        </View>
        {error.name && <Text style={styles.error}>{error.name}</Text>}
        <Text style={styles.label}>Email</Text>

        <View style={[styles.boxInput]}>
          <TextInput
            style={styles.input}
            onChangeText={(e) => {
              e &&
                setinput((prev) => {
                  return { ...prev, email: e };
                });
              seterror(FormValidation({ ...input, email: e }));
            }}
          />
        </View>
        {error.email && <Text style={styles.error}>{error.email}</Text>}
        <Text style={styles.label}>Password</Text>
        <View style={[styles.boxInput]}>
          <TextInput
            style={styles.input}
            onChangeText={(e) => {
              e &&
                setinput((prev) => {
                  return { ...prev, password: e }
                });
              seterror(FormValidation({ ...input, password: e }));
            }}
          />
        </View>
        {error.password && <Text style={styles.error}>{error.password}</Text>}
      </View>
      {input.name && input.email && input.password && (
        <Button
          title={"Guardar Cambios"}
          color={"green"}
          disabled={error.name || error.email || error.password ? true : false}
          onPress={handleClick}
        />
      )}
    </SafeAreaView>
  );
}

const styles = new StyleSheet.create({
  container: {
    width: "90%",
    alignItems: "center",
    marginVertical: 20,
    padding: 20,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    backgroundColor: "white",
    borderRadius: 20,
    alignItems: "center",
  },
  boxInput: {
    width: "100%",
    borderColor: "#EEEEEE",
    borderWidth: 3,
    marginVertical: 8,
    borderRadius: 10,
    alignItems: "center",
  },
  input: {
    fontSize: 20,
    width: "90%",
    paddingHorizontal: 4,
    textAlign: "center",
    color: "grey",
    paddingVertical: 10,
  },
  label: {
    alignSelf: "flex-start",
    paddingLeft: 20,
    color: "#bebccc",
  },
  header: {
    width: "80%",
    marginVertical: 30,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  title: {
    fontSize: 40,
    fontWeight: "bold",
  },
  error: {
    color: "#CB4535",
    fontSize: 11,
    alignSelf: "flex-end",
    position: "relative",
  },
});
