import react, { useState } from "react";
import { SafeAreaView, StyleSheet, TextInput } from "react-native";
import { View } from "react-native";
import CustomButton from "../components/CustomButton";

export default function Home() {
  const [user, setUser] = useState({});

  const handleInputName = (e) => {
    setUser((prev) => {
      return { ...prev, name: e };
    });
  };
  const handleInputPass = (e) => {
    setUser((prev) => {
      return { ...prev, password: e };
    });
  };
  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.boxInput}>
        <TextInput
          style={styles.input}
          placeholder="Email"
          onChangeText={handleInputName}
        />
      </View>
      <View style={styles.boxInput}>
        <TextInput
          style={styles.input}
          secureTextEntry={true}
          placeholder="password"
          onChangeText={handleInputPass}
        />
      </View>
      <View>
        {" "}
        <CustomButton text="Iniciar" colorb={"dodgerblue"} />
      </View>
    </SafeAreaView>
  );
}

const styles = new StyleSheet.create({
  container: {
    flex: 0.5,
    width: "100%",
    alignItems: "center",
  },
  boxInput: {
    width: "80%",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    backgroundColor: "white",
    marginVertical: 10,
    borderRadius: 20,
    alignItems: "center",
  },
  input: {
    fontSize: 20,
    width: "90%",
    paddingHorizontal: 4,
    textAlign: "center",
    paddingVertical: 10,
  },
});
