import axios from "axios";
import react, { useEffect, useState } from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  SafeAreaView,
  View,
  Button,
} from "react-native";
import { useNavigate, useParams } from "react-router-native";

export default function Acount() {
  const navigate = useNavigate();
  const { id } = useParams();
  const [user, setUser] = useState({});
  const [editable, setEditable] = useState(false);
  const [input, setinput] = useState({});

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/user/${id}`)
      .then((e) => setUser(e.data));
  }, []);

  const handleClick = () => {
    axios.put(`http://localhost:8000/api/user/${id}`, input).then((e) => {
      setUser(e.data);
      alert(`${e.data.name} tu perfil ha sido actualizado`);
    });
  };

  if (user) {
    return (
      <SafeAreaView
        style={{
          flex: 1,
          width: "100%",
          padding: 20,
          alignItems: "center",
        }}
      >
        <View style={styles.header}>
          <Text style={styles.title}>Perfil</Text>
          <Button title="Cerrar sesion" onPress={() => navigate("/register")} />
        </View>

        <View style={[styles.container]}>
          <Text style={styles.label}>Nombre</Text>

          <View
            style={[
              styles.boxInput,
              ,
              { backgroundColor: editable ? "white" : "#F7F7F7" },
            ]}
          >
            <TextInput
              style={styles.input}
              defaultValue={user.email}
              editable={editable}
              selectTextOnFocus={false}
              onChangeText={(e) =>
                setinput((prev) => {
                  return { ...prev, email: e };
                })
              }
            />
          </View>
          <Text style={styles.label}>Email</Text>

          <View
            style={[
              styles.boxInput,
              ,
              { backgroundColor: editable ? "white" : "#F7F7F7" },
            ]}
          >
            <TextInput
              style={styles.input}
              defaultValue={user.name}
              editable={editable}
              onChangeText={(e) =>
                setinput((prev) => {
                  return { ...prev, name: e };
                })
              }
            />
          </View>
          <Text style={styles.label}>Password</Text>
          <View
            style={[
              styles.boxInput,
              ,
              { backgroundColor: editable ? "white" : "#F7F7F7" },
            ]}
          >
            <TextInput
              style={styles.input}
              defaultValue={user.password}
              editable={editable}
              onChangeText={(e) =>
                setinput((prev) => {
                  return { ...prev, password: e };
                })
              }
            />
          </View>
          <View style={{ alignSelf: "flex-end" }}>
            <Button
              title={editable ? "No editar" : "Editar"}
              color={editable ? "#FFBF0F" : "green"}
              onPress={() => setEditable((prev) => !prev)}
            />
          </View>
        </View>
        {editable && (
          <Button
            title={"Guardar Cambios"}
            color={"green"}
            onPress={handleClick}
          />
        )}
      </SafeAreaView>
    );
  } else {
    return <Text>Loading</Text>;
  }
}

const styles = new StyleSheet.create({
  container: {
    width: "90%",
    alignItems: "center",
    marginVertical: 20,
    padding: 20,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    backgroundColor: "white",
    borderRadius: 20,
    alignItems: "center",
  },
  boxInput: {
    width: "100%",
    borderColor: "#EEEEEE",
    borderWidth: 3,
    marginVertical: 8,
    borderRadius: 10,
    alignItems: "center",
  },
  input: {
    fontSize: 20,
    width: "90%",
    paddingHorizontal: 4,
    textAlign: "center",
    paddingVertical: 10,
  },
  label: {
    alignSelf: "flex-start",
    paddingLeft: 20,
    color: "#bebccc",
  },
  header: {
    width: "80%",
    marginVertical: 30,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  title: {
    fontSize: 40,
    fontWeight: "bold",
  },
});
