import axios from "axios";
import react, { useEffect, useState } from "react";
import { StyleSheet, Text, TextInput, SafeAreaView, View } from "react-native";
import { useParams } from "react-router-native";

export default function Acount() {
  const { id } = useParams();
  const [user, setUser] = useState({});

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/user/${id}`)
      .then((e) => setUser(e.data));
  }, []);

  if (user) {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.boxInput}>
          <TextInput style={styles.input} defaultValue={user.email} />
        </View>
        <View style={styles.boxInput}>
          <TextInput style={styles.input} defaultValue={user.name} />
        </View>
        <View style={styles.boxInput}>
          <TextInput style={styles.input} defaultValue={user.password} />
        </View>
      </SafeAreaView>
    );
  } else {
    return <Text>Loading</Text>;
  }
}

const styles = new StyleSheet.create({
  container: {
    flex: 1,
    width: "100%",
    alignItems: "center",
  },
  boxInput: {
    width: "80%",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    backgroundColor: "white",
    marginVertical: 10,
    borderRadius: 20,
    alignItems: "center",
  },
  input: {
    fontSize: 20,
    width: "90%",
    paddingHorizontal: 4,
    textAlign: "center",
    paddingVertical: 10,
  },
});
