import react from "react";
import { TouchableOpacity, StyleSheet, Text } from "react-native";
import { Link } from "react-router-native";
export default function CustomButton({ text, onPress, colorb, to }) {
  return (
    <Link to={to}>
      <TouchableOpacity
        style={[styles.button, { backgroundColor: colorb }]}
        onPress={onPress}
      >
        <Text style={styles.appButtonText}>{text}</Text>
      </TouchableOpacity>
    </Link>
  );
}

const styles = new StyleSheet.create({
  button: {
    paddingVertical: 12,
    paddingHorizontal: 40,
  },
  appButtonText: {
    fontSize: 15,
    color: "#fff",
    fontWeight: "bold",
    alignSelf: "center",
  },
});
